class PapaConQueso{
    papaLog = (message) =>{
        const length1=message.length;
        message=message.replace(/(\r\n|\n|\r)/gm, "");
        const length2=message.length;
        if(length2>41){
            console.log("\x1b[31m", `

            _______   
           /       \\   
          |  >    < | TEXTO DEMASIADO LARGO PARA PODER SER COMUNICADO POR UNA PAPA 
          |     O   | 
           \\_______/         
      `)
        }
        else if(length1>length2){
            console.log("\x1b[31m", `

            _______   
           /       \\   
          |  >    < | PAPA CON QUESO ENOJADA DICE: ${message}  
          |     O   | 
           \\_______/         
      `)
        }
        else{
            console.log("\x1b[33m", `
              _______   
             /       \\   
            |  n    n | PAPA CON QUESO DICE: ${message}  
            |     O   | 
             \\_______/         
        `)
        }
    }
}

export default new PapaConQueso();